mapper.update:
		update oper_info 


    			 mid = #{mid},


    			 oper_id = #{operId},


    			 oper_pass = #{operPass},


    			 oper_name = #{operName},


    			 oper_tel = #{operTel},


    			 oper_email = #{operEmail},


    			 reg_date = #{regDate},


    			 state = #{state},


    			 logined = #{logined},


    			 mtype = #{mtype},


    			 auth = #{auth},


    			 role = #{role},


    			 err_count = #{errCount},


    			 last_updat = #{lastUpdat},


    			 err_time = #{errTime},


    			 inner_mercode = #{innerMercode},


		where
		1=1

    			and  mid = #{mid}


    			and  oper_id = #{operId}


    			and  oper_pass = #{operPass}


    			and  oper_name = #{operName}


    			and  oper_tel = #{operTel}


    			and  oper_email = #{operEmail}


    			and  reg_date = #{regDate}


    			and  state = #{state}


    			and  logined = #{logined}


    			and  mtype = #{mtype}


    			and  auth = #{auth}


    			and  role = #{role}


    			and  last_updat = #{lastUpdat}


    			and  err_time = #{errTime}


    			and  inner_mercode = #{innerMercode}

mapper.delete:
		delete from oper_info where 1=1

    			and mid = #{mid}


    			and oper_id = #{operId}


    			and oper_pass = #{operPass}


    			and oper_name = #{operName}


    			and oper_tel = #{operTel}


    			and oper_email = #{operEmail}


    			and reg_date = #{regDate}


    			and state = #{state}


    			and logined = #{logined}


    			and mtype = #{mtype}


    			and auth = #{auth}


    			and role = #{role}


    			and err_count = #{errCount}


    			and last_updat = #{lastUpdat}


    			and err_time = #{errTime}


    			and inner_mercode = #{innerMercode}
