mapper.select:
		select * from users t
		where 1=1 
    		
    			 and t.id = #{id}
    		
    		
    			 and t.user_name = #{userName}
    		
    		
    			 and t.user_password = #{userPassword}
    		
    		
    			 and t.role_type = #{roleType}
    		
    		
    			 and t.status = #{status}
    		
    		
    			 and t.last_login_time = #{lastLoginTime}
    		
        limit #{startRow},#{limit}

		select count(*) from users t
		where 1=1 
    		
    			 and t.id = #{id}
    		
    		
    			 and t.user_name = #{userName}
    		
    		
    			 and t.user_password = #{userPassword}
    		
    		
    			 and t.role_type = #{roleType}
    		
    		
    			 and t.status = #{status}
    		
    		
    			 and t.last_login_time = #{lastLoginTime}

		select * from users t
		where 1=1 
    		
    			 and t.id = #{id}
    		
    		
    			 and t.user_name = #{userName}
    		
    		
    			 and t.user_password = #{userPassword}
    		
    		
    			 and t.role_type = #{roleType}
    		
    		
    			 and t.status = #{status}
    		
    		
    			 and t.last_login_time = #{lastLoginTime}

mapper.update:
		update users 
		 
	    		
	    			 t.id = #{id},
	    		
	    		
	    			 t.user_name = #{userName},
	    		
	    		
	    			 t.user_password = #{userPassword},
	    		
	    		
	    			 t.role_type = #{roleType},
	    		
	    		
	    			 t.status = #{status},
	    		
	    		
	    			 t.last_login_time = #{lastLoginTime},
	    		
         
		where
		1=1
    		
    			and  t.id = #{id}
    		
    		
    			and  t.user_name = #{userName}
    		
    		
    			and  t.user_password = #{userPassword}
    		
    		
    			and  t.role_type = #{roleType}
    		
    		
    			and  t.status = #{status}
    		
    		
    			and  t.last_login_time = #{lastLoginTime}

mapper.delete:
		delete from users where 1=1
    		
    			and t.id = #{id}
    		
    		
    			and t.user_name = #{userName}
    		
    		
    			and t.user_password = #{userPassword}
    		
    		
    			and t.role_type = #{roleType}
    		
    		
    			and t.status = #{status}
    		
    		
    			and t.last_login_time = #{lastLoginTime}
